parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "nivel_identificado": {
      "type": "integer",
      "enum": [1, 2, 3, 4, 5],
      "description": "Número do nível de consciência (1-5)"
    },
    "name": {
      "type": "string",
      "enum": ["MOST AWARE", "PRODUCT AWARE", "PROBLEM AWARE", "SOLUTION AWARE", "UNAWARE"],
      "description": "Nome descritivo do nível de consciência identificado"
    },
    "confianca": {
      "type": "integer",
      "minimum": 0,
      "maximum": 100,
      "description": "Nível de confiança da classificação em porcentagem (0-100)"
    },
    "resumo_justificativa": {
      "type": "string",
      "description": "Análise resumida: foco principal, especificidade, validação da escolha"
    }
  },
  "required": ["nivel_identificado", "name", "confianca", "resumo_justificativa"],
  "additionalProperties": false
}

