parameters.inputSchema:
{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "type": "object",
  "properties": {
    "Novos_Angulos": {
      "type": "array",
      "minItems": 5,
      "maxItems": 5,
      "items": {
        "type": "object",
        "properties": {
          "nivel_consciencia": {
            "type": "string",
            "enum": [
              "Público Mais Consciente",
              "Público Consciente do Produto, Mas Não Convencido",
              "Público Consciente do Desejo, Mas Não do Produto", 
              "Público Consciente do Problema, Mas Não do Desejo",
              "Público Completamente Inconsciente"
            ],
            "description": "Nível de consciência do público-alvo para este ângulo"
          },
          "angulo": {
            "type": "string",
            "description": "Descrição do ângulo de marketing proposto"
          },
          "headline": {
            "type": "string",
            "description": "Headline criada para capturar a atenção deste nível de consciência"
          },
          "justificativa_schwartz": {
            "type": "string",
            "description": "Justificativa detalhada baseada na metodologia de Eugene Schwartz"
          }
        },
        "required": ["nivel_consciencia", "angulo", "headline", "justificativa_schwartz"],
        "additionalProperties": false
      }
    }
  },
  "required": ["novos_angulos"],
  "additionalProperties": false
}

{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "type": "object",
  "title": "Análise Schwartz de Copywriting",
  "description": "Schema para análise e correção de copy baseada na metodologia de Eugene Schwartz com 5 pontos de melhoria",
  "properties": {
    "Analise_Schwartz": {
      "type": "array",
      "description": "Array com exatamente 5 análises de melhoria baseadas na metodologia de Eugene Schwartz",
      "minItems": 5,
      "maxItems": 5,
      "items": {
        "type": "object",
        "properties": {
          "erro": {
            "type": "string",
            "description": "Identificação de um erro ou falha específica na copy segundo os princípios de Schwartz"
          },
          "motivo": {
            "type": "string",
            "description": "Explicação detalhada do porquê esse erro compromete a eficácia da copy"
          },
          "ajuste_metodologia_schwartz": {
            "type": "string",
            "description": "Metodologia específica de Schwartz que deve ser aplicada para corrigir o erro identificado"
          },
          "exemplo_reescrito": {
            "type": "string",
            "description": "Versão corrigida do trecho problemático aplicando a metodologia de Schwartz sugerida"
          }
        },
        "required": ["erro", "motivo", "ajuste_metodologia_schwartz", "exemplo_reescrito"],
        "additionalProperties": false
      }
    }
  },
  "required": ["analise_schwartz"],
  "additionalProperties": false
}
